const sidebar=document.querySelector(".sidebar"),iconToggleSidebar=document.querySelector(".toggle-sidebar"),mainContent=document.querySelector(".content-area");iconToggleSidebar.addEventListener("click",()=>{sidebar.classList.toggle("active"),mainContent.classList.toggle("active")});const headMenuLink=Array.from(document.querySelectorAll(".sidebar__menuLinks > h3.headLink"));headMenuLink.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();const n=document.querySelector(`.sidebar__menuLinks .${e.dataset.menu}`);document.querySelector(`.sidebar__menuLinks .headLink[data-menu=${e.dataset.menu}] > .icon-toggle`).classList.toggle("rotatechevron"),n.classList.toggle("active")})});